import React from "react";

const pics = [
    'url("https://brave-pebble-0432f1103.2.azurestaticapps.net/src/kat1.png")',
    'url("https://brave-pebble-0432f1103.2.azurestaticapps.net/src/kat2.png")',
    'url("https://brave-pebble-0432f1103.2.azurestaticapps.net/src/kat3.png")',
    'url("https://brave-pebble-0432f1103.2.azurestaticapps.net/src/kat4.png")',
    'url("https://brave-pebble-0432f1103.2.azurestaticapps.net/src/kat5.png")'
];

function showImage() {
    var a = Math.floor(Math.random() * pics.length);
    var img = pics[a];
    document.getElementById("img").src=img;
}

class Games extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            counter: 0,
        }
    }
    increment () {
        this.setState({
            counter: this.state.counter + 1,
        })
    }

    render(){
        const {cost} = this.props;
        return(
                <div className="responsive">
                    <div className="gallery">
                        <a target="_blank" href="src/kat4.png"><img id="img" src="https://brave-pebble-0432f1103.2.azurestaticapps.net/src/kat5.png" alt="spel" /></a>
                        <div className="desc">
                        <h2>{cost}</h2>
                        <br/>
                        <h2>du har {this.state.counter}st i korgen</h2>
                        <button className="buy" onClick={() => this.increment()}>KÃ¶p</button>
                        </div>
                    </div>
                </div>
        )
    }
}
export default Games;
